import { v4 as uuidv4 } from 'uuid';
import { ConversationSession, Message } from './types';
import { ConfigManager } from './config';
export class ConversationManager {
private currentSession: ConversationSession | null = null;
private configManager = ConfigManager.getInstance();
startNewSession(): void {
const config = this.configManager.getConfig();
const now = Date.now();
this.currentSession = {
sessionId: `gh-copilot-${now}-${uuidv4().substring(0, 8)}`,
platform: 'github_copilot',
userIdentifier: config.userIdentifier,
conversationTitle: '',
conversationContext: '',
messages: [],
topics: [],
status: 'active',
metadata: {
startedAt: now,
lastMessageAt: now,
totalTokens: 0,
vscodeVersion: vscode.version,
workspaceFolder: vscode.workspace.workspaceFolders?.[0]?.uri.fsPath
}
};
}
addMessage(message: Message): void {
if (!this.currentSession) {
this.startNewSession();
}
this.currentSession!.messages.push(message);
this.currentSession!.metadata.lastMessageAt = Date.now();
this.currentSession!.metadata.totalTokens += message.tokens;
// Auto-generate title from first user message
if (!this.currentSession!.conversationTitle && message.role === 'user') {
this.currentSession!.conversationTitle = this.generateTitle(message.content);
}
// Extract topics
this.updateTopics(message.content);
}
private generateTitle(firstMessage: string): string {
// Take first 50 chars or first sentence
const title = firstMessage
.split(/[.!?]/)[0]
.substring(0, 50)
.trim();
return title || 'Copilot Conversation';
}
private updateTopics(content: string): void {
// Simple topic extraction (can be enhanced with NLP)
const keywords = ['database', 'function', 'class', 'API', 'error', 'bug', 'performance', 'security'];
const lowerContent = content.toLowerCase();
keywords.forEach(keyword => {
if (lowerContent.includes(keyword) && !this.currentSession!.topics.includes(keyword)) {
this.currentSession!.topics.push(keyword);
}
});
}
getCurrentSession(): ConversationSession | null {
return this.currentSession;
}
hasUnsavedMessages(): boolean {
return this.currentSession !== null &&
this.currentSession.messages.length > 0 &&
this.currentSession.status === 'active';
}
markAsCompleted(): void {
if (this.currentSession) {
this.currentSession.status = 'completed';
}
}
clearSession(): void {
this.currentSession = null;
}
}
