import axios, { AxiosError } from 'axios';
import * as vscode from 'vscode';
import { ConversationSession } from './types';
import { ConfigManager } from './config';
export class Recorder {
private configManager = ConfigManager.getInstance();
private retryCount = 3;
private retryDelay = 1000;
async saveConversation(session: ConversationSession): Promise<boolean> {
const config = this.configManager.getConfig();
if (!config.enabled) {
console.log('[Copilot Recorder] Recording disabled');
return false;
}
try {
// Build conversation context (summary)
const context = this.buildConversationContext(session);
session.conversationContext = context;
// Send to API
const response = await this.sendWithRetry(config.apiEndpoint, session);
if (response.success) {
if (config.showNotifications) {
vscode.window.showInformationMessage(
`✅ Copilot conversation saved (ID: ${response.conversation_id})`
);
}
return true;
} else {
throw new Error(response.error || 'Unknown error');
}
} catch (error) {
console.error('[Copilot Recorder] Save failed:', error);
if (config.showNotifications) {
vscode.window.showErrorMessage(
`❌ Failed to save conversation: ${this.getErrorMessage(error)}`
);
}
return false;
}
}
private buildConversationContext(session: ConversationSession): string {
const lines: string[] = [];
lines.push(`# Copilot Conversation - ${session.conversationTitle}`);
lines.push(`Session ID: ${session.sessionId}`);
lines.push(`Started: ${new Date(session.metadata.startedAt).toISOString()}`);
lines.push(`Messages: ${session.messages.length}`);
lines.push(`Total Tokens: ${session.metadata.totalTokens}`);
lines.push('');
session.messages.forEach((msg, idx) => {
lines.push(`## Message ${idx + 1} (${msg.role})`);
lines.push(msg.content);
lines.push('');
});
return lines.join('\n');
}
private async sendWithRetry(url: string, data: any, attempt: number = 1): Promise<any> {
try {
const response = await axios.post(url, data, {
headers: {
'Content-Type': 'application/json'
},
timeout: 30000
});
return response.data;
} catch (error) {
if (attempt < this.retryCount) {
console.log(`[Copilot Recorder] Retry ${attempt}/${this.retryCount}`);
await this.sleep(this.retryDelay * attempt);
return this.sendWithRetry(url, data, attempt + 1);
}
throw error;
}
}
private sleep(ms: number): Promise<void> {
return new Promise(resolve => setTimeout(resolve, ms));
}
private getErrorMessage(error: any): string {
if (axios.isAxiosError(error)) {
return error.response?.data?.error || error.message;
}
return error.message || String(error);
}
}
