export class TokenEstimator {
/**
* Estimate token count for text
* Based on rough approximation: ~4 chars per token
*/
estimate(text: string): number {
if (!text) return 0;
// Character count
const charCount = text.length;
// Code blocks add overhead
const codeBlocks = (text.match(/```[\s\S]*?```/g) || []).length;
const codeBlockOverhead = codeBlocks * 10;
// JSON structures add overhead
const jsonBlocks = (text.match(/\{[\s\S]*?\}/g) || []).length;
const jsonOverhead = jsonBlocks * 5;
// Base estimation
const baseTokens = Math.ceil(charCount / 4);
return baseTokens + codeBlockOverhead + jsonOverhead;
}
}
