import * as vscode from 'vscode';
import { ConversationManager } from './conversationManager';
import { Recorder } from './recorder';
import { TokenEstimator } from './tokenEstimator';
import { StatusBarManager } from './statusBar';
import { ConfigManager } from './config';
let conversationManager: ConversationManager;
let recorder: Recorder;
let tokenEstimator: TokenEstimator;
let statusBar: StatusBarManager;
let autoSaveTimer: NodeJS.Timeout | null = null;
export function activate(context: vscode.ExtensionContext) {
console.log('[Copilot Recorder] Activating extension...');
// Initialize managers
conversationManager = new ConversationManager();
recorder = new Recorder();
tokenEstimator = new TokenEstimator();
statusBar = new StatusBarManager();
// Register commands
context.subscriptions.push(
vscode.commands.registerCommand('copilot-recorder.saveCurrentConversation', saveCurrentConversation),
vscode.commands.registerCommand('copilot-recorder.clearSession', clearSession),
vscode.commands.registerCommand('copilot-recorder.showStats', showStats),
vscode.commands.registerCommand('copilot-recorder.configure', configure)
);
// Start auto-save timer
startAutoSave();
// Monitor chat activity (fallback method)
monitorChatActivity();
console.log('[Copilot Recorder] Extension activated!');
}
export function deactivate() {
if (autoSaveTimer) {
clearInterval(autoSaveTimer);
}
statusBar.dispose();
}
function startAutoSave(): void {
const config = ConfigManager.getInstance().getConfig();
if (autoSaveTimer) {
clearInterval(autoSaveTimer);
}
autoSaveTimer = setInterval(async () => {
if (conversationManager.hasUnsavedMessages()) {
const session = conversationManager.getCurrentSession();
if (session && session.messages.length >= config.minMessagesBeforeSave) {
await recorder.saveConversation(session);
conversationManager.markAsCompleted();
}
}
}, config.autoSaveInterval);
}
async function saveCurrentConversation(): Promise<void> {
const session = conversationManager.getCurrentSession();
if (!session || session.messages.length === 0) {
vscode.window.showWarningMessage('No active conversation to save');
return;
}
statusBar.setRecording(true);
const success = await recorder.saveConversation(session);
statusBar.setRecording(false);
if (success) {
conversationManager.markAsCompleted();
updateStatusBar();
}
}
function clearSession(): void {
conversationManager.clearSession();
statusBar.updateStats(0, 0);
vscode.window.showInformationMessage('Conversation session cleared');
}
function showStats(): void {
const session = conversationManager.getCurrentSession();
if (!session) {
vscode.window.showInformationMessage('No active conversation');
return;
}
const stats = [
`Session ID: ${session.sessionId}`,
`Messages: ${session.messages.length}`,
`Total Tokens: ~${session.metadata.totalTokens}`,
`Started: ${new Date(session.metadata.startedAt).toLocaleString()}`,
`Topics: ${session.topics.join(', ') || 'None detected'}`
];
vscode.window.showInformationMessage(stats.join('\n'));
}
async function configure(): Promise<void> {
const config = ConfigManager.getInstance().getConfig();
const option = await vscode.window.showQuickPick([
{ label: 'Toggle Recording', description: config.enabled ? 'Currently ON' : 'Currently OFF' },
{ label: 'Change API Endpoint', description: config.apiEndpoint },
{ label: 'Change User Identifier', description: config.userIdentifier },
{ label: 'Toggle Notifications', description: config.showNotifications ? 'Currently ON' : 'Currently OFF' }
]);
if (!option) return;
// Handle configuration changes
// (Implementation left as exercise)
}
function monitorChatActivity(): void {
// Strategy 1: Monitor active editor (fallback)
vscode.window.onDidChangeActiveTextEditor(editor => {
if (editor?.document.uri.scheme === 'copilot-chat') {
// Copilot chat window active
console.log('[Copilot Recorder] Chat window active');
}
});
// Strategy 2: Monitor workspace changes
vscode.workspace.onDidChangeTextDocument(event => {
// Check if document is chat-related
if (event.document.uri.toString().includes('copilot')) {
const text = event.document.getText();
// Try to parse conversation from document
parseConversationFromText(text);
}
});
}
function parseConversationFromText(text: string): void {
// Parse conversation from chat window text
// This is a simplified version - would need refinement
const userMessagePattern = /You:\s*(.*?)(?=\n\nCopilot:|$)/gs;
const copilotMessagePattern = /Copilot:\s*(.*?)(?=\n\nYou:|$)/gs;
let match;
while ((match = userMessagePattern.exec(text)) !== null) {
const content = match[1].trim();
conversationManager.addMessage({
role: 'user',
content,
tokens: tokenEstimator.estimate(content),
timestamp: Date.now()
});
}
while ((match = copilotMessagePattern.exec(text)) !== null) {
const content = match[1].trim();
conversationManager.addMessage({
role: 'assistant',
content,
tokens: tokenEstimator.estimate(content),
timestamp: Date.now()
});
}
updateStatusBar();
}
function updateStatusBar(): void {
const session = conversationManager.getCurrentSession();
if (session) {
statusBar.updateStats(
session.messages.length,
session.metadata.totalTokens
);
} else {
statusBar.updateStats(0, 0);
}
}
