    font-weight: 700;
    margin-bottom: 0.5rem;
    text-shadow: 0 2px 4px rgba(0,0,0,0.2);
}

.dashboard-header p {
    font-size: 1.1rem;
    opacity: 0.9;
    margin-bottom: 0;
}

/* Service Status Pills */
.status-pills {
    display: flex;
    gap: 1rem;
    margin-bottom: 2rem;
    flex-wrap: wrap;
}

.status-pill {
    background: white;
    border-radius: var(--radius-lg);
    padding: 1rem 1.5rem;
    box-shadow: var(--shadow-md);
    transition: var(--transition-medium);
    cursor: pointer;
    flex: 1;
    min-width: 200px;
    position: relative;
    overflow: hidden;
}

.status-pill::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: var(--gradient-primary);
    transition: var(--transition-medium);
}

.status-pill:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-lg);
}

.status-pill.success::before { background: var(--gradient-success); }
.status-pill.warning::before { background: var(--gradient-warning); }
.status-pill.danger::before { background: var(--gradient-danger); }
.status-pill.info::before { background: var(--gradient-info); }

.status-pill-icon {
    font-size: 2rem;
    margin-bottom: 0.5rem;
    display: block;
}

.status-pill-title {
    font-size: 0.9rem;
    font-weight: 600;
    color: var(--medium-gray);
    margin-bottom: 0.25rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.status-pill-value {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--dark-gray);
}

/* Dashboard Cards */
.dashboard-card {
    background: white;
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-md);
    transition: var(--transition-medium);
    overflow: hidden;
    margin-bottom: 1.5rem;
}

.dashboard-card:hover {
    box-shadow: var(--shadow-lg);
}

.dashboard-card-header {
    background: var(--light-gray);
    padding: 1.25rem 1.5rem;
    border-bottom: 1px solid #dee2e6;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.dashboard-card-header h5 {
    margin: 0;
    font-weight: 600;
    color: var(--dark-gray);
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.dashboard-card-body {
    padding: 1.5rem;
}

.dashboard-card-actions {
    display: flex;
    gap: 0.5rem;
}

/* Action Buttons */
.action-btn {
    border: none;
    border-radius: var(--radius-md);
    padding: 0.5rem 1rem;
    font-weight: 500;
    cursor: pointer;
    transition: var(--transition-fast);
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    text-decoration: none;
    font-size: 0.875rem;
}

.action-btn:hover {
    transform: translateY(-1px);
    text-decoration: none;
}

.action-btn-primary {
    background: var(--gradient-primary);
    color: white;
    box-shadow: var(--shadow-sm);
}

.action-btn-secondary {
    background: var(--gradient-secondary);
    color: white;
    box-shadow: var(--shadow-sm);
}

.action-btn-success {
    background: var(--gradient-success);
    color: white;
    box-shadow: var(--shadow-sm);
}

.action-btn-warning {
    background: var(--gradient-warning);
    color: white;
    box-shadow: var(--shadow-sm);
}

.action-btn-danger {
    background: var(--gradient-danger);
    color: white;
    box-shadow: var(--shadow-sm);
}

.action-btn-outline {
    background: transparent;
    color: var(--primary-blue);
    border: 2px solid var(--primary-blue);
}

.action-btn-outline:hover {
    background: var(--primary-blue);
    color: white;
}

/* Modern Tables */
.modern-table {
    width: 100%;
    border-collapse: collapse;
    background: white;
    border-radius: var(--radius-md);
    overflow: hidden;
    box-shadow: var(--shadow-sm);
}

.modern-table th {
    background: var(--light-gray);
    padding: 1rem;
    font-weight: 600;
    color: var(--dark-gray);
    text-align: left;
    border-bottom: 2px solid #dee2e6;
    font-size: 0.875rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.modern-table td {
    padding: 1rem;
    border-bottom: 1px solid #f1f3f4;
    vertical-align: middle;
}

.modern-table tbody tr:hover {
    background: rgba(0,123,255,0.02);
}

.modern-table tbody tr:last-child td {
    border-bottom: none;
}

/* Status Badges */
.status-badge {
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.25rem 0.75rem;
    border-radius: var(--radius-lg);
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.status-badge-success {
    background: rgba(40, 167, 69, 0.1);
    color: var(--success-green);
}

.status-badge-warning {
    background: rgba(253, 126, 20, 0.1);
    color: var(--warning-orange);
}

.status-badge-danger {
    background: rgba(220, 53, 69, 0.1);
    color: var(--danger-red);
}

.status-badge-info {
    background: rgba(23, 162, 184, 0.1);
    color: var(--info-cyan);
}

.status-badge-secondary {
    background: rgba(111, 66, 193, 0.1);
    color: var(--secondary-purple);
}

/* Progress Bars */
.progress-modern {
    height: 8px;
    background: #e9ecef;
    border-radius: var(--radius-lg);
    overflow: hidden;
    margin: 0.5rem 0;
}

.progress-modern-bar {
    height: 100%;
    background: var(--gradient-primary);
    transition: width var(--transition-medium);
    position: relative;
}

.progress-modern-bar::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    animation: shimmer 2s infinite;
}

@keyframes shimmer {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

/* Metrics Grid */
.metrics-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
    margin: 1.5rem 0;
}

.metric-card {
    background: white;
    border-radius: var(--radius-lg);
    padding: 1.5rem;
    box-shadow: var(--shadow-md);
    text-align: center;
    transition: var(--transition-medium);
    position: relative;
    overflow: hidden;
}

.metric-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: var(--gradient-primary);
}

.metric-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-xl);
}

.metric-icon {
    font-size: 3rem;
    margin-bottom: 1rem;
    color: var(--primary-blue);
}

.metric-value {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--dark-gray);
    margin-bottom: 0.5rem;
}

.metric-label {
    font-size: 0.875rem;
    color: var(--medium-gray);
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.metric-change {
    font-size: 0.75rem;
    margin-top: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.25rem;
}

.metric-change.positive {
    color: var(--success-green);
}

.metric-change.negative {
    color: var(--danger-red);
}

/* Quick Actions Panel */
.quick-actions {
    background: white;
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-md);
    padding: 1.5rem;
    margin-bottom: 1.5rem;
}

.quick-actions h5 {
    margin-bottom: 1rem;
    color: var(--dark-gray);
    font-weight: 600;
}

.quick-actions-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
}

.quick-action {
    background: var(--light-gray);
    border-radius: var(--radius-md);
    padding: 1rem;
    text-align: center;
    transition: var(--transition-medium);
    cursor: pointer;
    border: 2px solid transparent;
}

.quick-action:hover {
    background: white;
    border-color: var(--primary-blue);
    transform: translateY(-2px);
    box-shadow: var(--shadow-md);
}

.quick-action-icon {
    font-size: 2rem;
    margin-bottom: 0.5rem;
    color: var(--primary-blue);
}

.quick-action-title {
    font-weight: 600;
    color: var(--dark-gray);
    font-size: 0.875rem;
}

/* Responsive Design */
@media (max-width: 768px) {
    .dashboard-header {
        padding: 1.5rem 0;
    }

    .dashboard-header h1 {
        font-size: 2rem;
    }

    .status-pills {
        flex-direction: column;
    }

    .status-pill {
        min-width: auto;
    }

    .metrics-grid {
        grid-template-columns: 1fr;
    }

    .quick-actions-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (max-width: 480px) {
    .dashboard-header h1 {
        font-size: 1.75rem;
    }

    .quick-actions-grid {
        grid-template-columns: 1fr;
    }

    .dashboard-card-body {
        padding: 1rem;
    }

    .modern-table th,
    .modern-table td {
        padding: 0.75rem 0.5rem;
        font-size: 0.875rem;
    }
}

/* Dark Mode Support */
@media (prefers-color-scheme: dark) {
    :root {
        --light-gray: #2d3748;
        --medium-gray: #a0aec0;
        --dark-gray: #f7fafc;
    }

    body {
        background: linear-gradient(135deg, #1a202c 0%, #2d3748 100%);
        color: var(--dark-gray);
    }

    .dashboard-card,
    .metric-card,
    .quick-actions {
        background: #2d3748;
        color: var(--dark-gray);
    }

    .dashboard-card-header {
        background: #4a5568;
    }

    .modern-table th {
        background: #4a5568;
        color: var(--dark-gray);
    }

    .modern-table {
        background: #2d3748;
    }

    .modern-table tbody tr:hover {
        background: rgba(255,255,255,0.05);
    }
}

/* Loading States */
.loading-skeleton {
    background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
    background-size: 200% 100%;
    animation: loading 1.5s infinite;
    border-radius: var(--radius-sm);
}

@keyframes loading {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
}

/* Animations */
.fade-in {
    animation: fadeIn 0.5s ease;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

.slide-up {
    animation: slideUp 0.3s ease;
}

@keyframes slideUp {
    from { transform: translateY(100%); opacity: 0; }
    to { transform: translateY(0); opacity: 1; }
}

